derermap.addListener('workspaceready',			function() {				// Preload the images required for iportal Warning Dialog on				// session timeout				iportal.preloadResources();				// Ext.get("iportal_LOGO").focus();				/** Checking whether piggyback is on or not**/				if (iportal.systempreferences.isAlertViaPiggyBack()) {					Ext.Ajax.on({						'beforerequest' : function(con, opt) {							var listner = new iportal.util.globalBeforeAjaxRequestListner();							listner.processPiggyReq(opt);						}					});					iportal.apps.alertPiggybackListner.processHighUnPoppedupAlerts();				}	});	iportal.preloadResources = function() {		var resource_preload = iportal.systempreferences.getPreloadResources();		var resource_preload_obj = Ext.decode(resource_preload);		var image_preload = resource_preload_obj['PRELOAD_KEY'];				if (document.images) {			for ( var index = 0; index < image_preload.length; index++) {				var preloaded_image = new Image(0, 0);				preloaded_image.src = image_preload[index];			}		}	};